catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }

	//alert('value is: '+g_form.getDisplayBox('select_the_application_to_be_deployed').value);
	var ans = new GlideAjax('ClientCallableUtils');
	ans.addParam("sysparm_name","fetchAppDetails");
	ans.addParam("appName",g_form.getDisplayBox('select_the_application_to_be_deployed').value);
	ans.getXMLAnswer(appDetails);

	function appDetails(answer){
		var op = JSON.parse(answer);
		if(op.status == "Success"){
			g_form.setValue('git_repository',op.repository_path);
			g_form.setValue('group_to_be_notified',op.maintained_by);
			g_form.setValue('atf_suite',op.atfSuites);
		}


	}



}
