sys_script_include.script:
var ClientCallableUtils = Class.create();
ClientCallableUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    fetchAppDetails: function() {

        gs.info('SI: function fetchAppDetails called.. ' + this.getParameter('appName'));
        var appName = this.getParameter('appName');

        //Query App Configs table to get app details
        var grXCC1856DS = new GlideRecord('x_cdltd_ccw1856_dev_scopes');
        grXCC1856DS.addEncodedQuery("name=" + appName);
        grXCC1856DS.query();

        var answer = {}; //store o/p of this function to be returned to client side

        if (grXCC1856DS.getRowCount() == 1) {
            if (grXCC1856DS.next()) {
                answer.status = "Success";
                answer.scope = grXCC1856DS.getValue('scope');
                answer.repository_path = grXCC1856DS.getValue('repository_path');
                answer.maintained_by = grXCC1856DS.getValue('maintained_by');

                //get ATF suites
				var atfSuites = this._fetchATFdetails(grXCC1856DS.sys_id);

                answer.atfSuites = atfSuites;
            }
            return JSON.stringify(answer);
        } else {
            answer.status = "Failure. There are no or more than 1 apps with same name. Contact System Admin.";
            return answer;
        }


    },

    _fetchATFdetails: function(appSysId) {
        var atfSuites = '';
        var grXCC1856ATA = new GlideRecord('x_cdltd_ccw1856_atf_to_apps');
        grXCC1856ATA.addEncodedQuery("application=" + appSysId);
        grXCC1856ATA.query();
        while (grXCC1856ATA.next()) {
            if (atfSuites.length == 0)
                atfSuites = grXCC1856ATA.getDisplayValue('suite');
            else
                atfSuites = atfSuites + "," + grXCC1856ATA.getDisplayValue('suite');
        }
		return atfSuites;
    },

	_fetchTeamSetup: function(appSysId){
		//TBD
	},

    type: 'ClientCallableUtils'
});
